import React, { Component } from 'react'

export default class BaiTap02 extends Component {
    constructor(){
        super();
        this.state = {
            valueInput: "son ",
        }
    }

    handleChange = (value) => {
       this.setState({
        valueInput: "!this.state.valueInput"
       })
     console.log(value.target.value);
    }
  render() {
    let { valueInput} = this.state;
    return (
      <div>
        <h2>BaiTap02</h2>
        <form>
            <input value={valueInput} onChange={(value)=>{this.handleChange(value)}} type='text'/>
            <button></button>
            <p>{valueInput}</p>
        </form>
        </div>
    )
  }
}
