import React, { Component } from 'react'

export default class BaiTap02 extends Component {
    constructor(){
        super();
        this.state = {
            valueInput: " ",
        }
    }

    handleChange = (event) => {
     console.log(event.target.value);
     this.setState = ({
        valueInput: "event.target.value"
     })
    }
  render() {
    let { valueInput} = this.state;
    return (
      <div>
        <h2>BaiTap02</h2>
        <form>
            <input value={valueInput} onChange={(event)=>{this.handleChange(event)}} type="text"/>
            <button></button>
            <p>{valueInput}</p>
        </form>
        </div>
    )
  }
}
