import React, { Component } from 'react'

export default class BaiTap02 extends Component {
    constructor(){
        super();
        this.state = {
            valueInput: " ",
        }
    }

    handleChange = (text) => {
      this.state({
        valueInput:text.value
      })
    }
  render() {
    return (
      <div>
        <h2>BaiTap02</h2>
        <form>
            <input value={this.state.valueInput} onChange={(text)=>{this.handleChange(text)}} type="text"></input>
            <button></button>
            <p>{this.state.valueInput}</p>
        </form>
        </div>
    )
  }
}
