import React, { Component } from 'react'

export default class BaiTap02 extends Component {
    constructor(){
        super();
        this.state = {
            valueInput: " ",
        }
    }

    handleChange = (e) => {
       console.log(e);
    }
  render() {
    let { valueInput} = this.state;
    return (
      <div>
        <h2>BaiTap02</h2>
        <form>
            <input type='text' value={valueInput} onChange={(e)=>{this.handleChange(e.target["value"])}} />
            <button></button>
            <p>{valueInput}</p>
        </form>
        </div>
    )
  }
}
